// src/components/MyNEOChart.js
import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

// Register the required Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function MyNEOChart({ neoData }) {
  // Prepare the chart data for visualization
  const data = {
    labels: neoData.map((neo) => neo.date), // Close approach dates
    datasets: [
      {
        label: 'Miss Distance (km)',
        data: neoData.map((neo) => neo.missDistance), // Miss distances in kilometers
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        fill: true,
        tension: 0.4, // Curved lines
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'NEO Close Approaches: Miss Distance over Time',
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Close Approach Date',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Miss Distance (km)',
        },
        beginAtZero: true,
      },
    },
  };

  return <Line data={data} options={options} />;
}

export default MyNEOChart;
