import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import './NasaLibrary.css'; // Ensure you have your CSS ready

function NasaLibrary() {
  const [searchTerm, setSearchTerm] = useState('');
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Function to fetch images based on the search term
  const fetchImages = async (query) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`/api/nasa-library?query=${query}`);
      const data = await response.json();
      setImages(data.collection.items); // Assuming the API structure returns items here
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    if (searchTerm.trim()) {
      fetchImages(searchTerm);
    }
  };

  useEffect(() => {
    if (searchTerm) {
      fetchImages(searchTerm); // Fetch images on search term change
    }
  }, [searchTerm]);

  return (
    <div className="nasa-library-container">
      <Navbar />
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {loading && <div className="spinner">Loading...</div>}
      {error && <p className="error">Error fetching images: {error.message}</p>}
      <div className="image-gallery">
        {images.length > 0 ? (
          images.map((item, index) => (
            <div key={index} className="image-item">
              {item?.links[0]?.href && ( // Check if link exists
                <img src={item.links[0].href} alt={item.data[0]?.title} className="image" />
              )}
              <p className="image-title">{item.data[0]?.title}</p> {/* Display title */}
            </div>
          ))
        ) : (
          <p>No results found.</p>
        )}
      </div>
    </div>
  );
}

export default NasaLibrary;
