import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar'; // Import Navbar
import './APOD.css';

function APOD() {
  const [apodData, setApodData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedDate, setSelectedDate] = useState(''); // State for selected date

  // Function to fetch APOD data
  const fetchAPOD = async (date) => {
    setLoading(true);
    try {
      console.log(`Fetching APOD for date: ${date}`); // Log the date being fetched
      const response = await fetch(`/api/apod?date=${date}`);
      const data = await response.json();
      console.log('Fetched APOD Data:', data); // Log the fetched data
      setApodData(data);
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  // Effect to fetch APOD when the component mounts or when selectedDate changes
  useEffect(() => {
    // Check if selectedDate is valid
    if (selectedDate) {
      fetchAPOD(selectedDate);
    } else {
      const today = new Date().toISOString().split('T')[0]; // Get today's date
      fetchAPOD(today); // Default to today's date
    }
  }, [selectedDate]);

  return (
    <div className="apod-container">
      <Navbar /> {/* Include the Navbar */}

      {loading && <div className="spinner">Loading...</div>}
      {error && <p className="error">Error fetching data: {error.message}</p>}

      <div className="apod-content">
        <h1>{apodData?.title || 'Astronomy Picture of the Day'}</h1>

        {/* Date selection */}
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)} // Update selected date
          style={{ margin: '20px 0', padding: '10px', fontSize: '1rem' }}
        />

        {/* Conditional rendering based on media type */}
        {apodData && apodData.media_type === 'image' ? (
          <img src={apodData.url} alt={apodData.title} className="apod-image" />
        ) : (
          apodData && (
            <iframe
              title="APOD Video"
              src={apodData.url}
              className="apod-video"
              frameBorder="0"
              allow="autoplay; encrypted-media"
              allowFullScreen
            />
          )
        )}

        <p className="description">{apodData?.explanation}</p>
        <p className="date">Date: {apodData?.date || new Date().toISOString().split('T')[0]}</p> {/* Display the date */}
      </div>
    </div>
  );
}

export default APOD;
