import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [apodData, setApodData] = useState(null);
  const [marsPhotos, setMarsPhotos] = useState([]);
  const [epicImages, setEpicImages] = useState([]);
  const [neoData, setNeoData] = useState([]);
  const [nasaLibrary, setNasaLibrary] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sol, setSol] = useState(1000); // Default Martian day

  // Fetch EPIC images
  const fetchEpicImages = async () => {
    setLoading(true); // Set loading state before fetching
    try {
      const response = await fetch('/api/epic'); // Fetch data from your backend
      const data = await response.json(); // Parse the response as JSON
      console.log('EPIC Images Data:', data); // Log the fetched data to the console

      // Adjust according to the actual response structure
      if (data && Array.isArray(data)) { // Make sure data is an array
        setEpicImages(data); // Set the state to the array of images
      } else {
        console.error('Unexpected data structure:', data);
        setEpicImages([]); // Set to empty if unexpected structure
      }
    } catch (error) {
      console.error('Error fetching EPIC images:', error); // Log any errors
      setError(error); // Set the error state
    } finally {
      setLoading(false); // Set loading to false after fetching
    }
  };

  // Call the fetch functions when the component mounts
  useEffect(() => {
    fetchEpicImages(); // Call the function to fetch EPIC images
    // Add other fetch functions here...
  }, []); // Remove [sol] if not needed for fetching EPIC images

  return (
    <div className="App">
      <h1>NASA Data Explorer</h1>

      {/* Display loading indicator */}
      {loading && <div className="spinner">Loading...</div>}

      {/* Display error message if there is an error */}
      {error && <p className="error">Error fetching data: {error.message}</p>}

      {/* EPIC Images Section */}
      <div className="epic-images">
        <h2>Earth Polychromatic Imaging Camera (EPIC) Images</h2>
        {epicImages.length > 0 ? (
          epicImages.map((image) => (
            <div key={image.identifier}>
              <img 
                src={`https://epic.gsfc.nasa.gov/archive/natural/${image.date.slice(0, 10)}/jpg/${image.image}.jpg`} 
                alt={image.caption} 
                style={{ width: '100%', height: 'auto' }} // Responsive images
              />
              <p>{image.caption}</p> {/* Display the caption for each image */}
            </div>
          ))
        ) : (
          <p>No EPIC images available.</p>
        )}
      </div>

      {/* Other sections... */}
    </div>
  );
}

export default App;
