import React, { useEffect, useState, useCallback } from 'react';
import Navbar from '../components/Navbar'; // Ensure the path is correct
import './MarsPhotos.css'; // Import the CSS for MarsPhotos

function MarsPhotos() {
  const [marsPhotos, setMarsPhotos] = useState([]);
  const [sol, setSol] = useState(1000); // Default Sol value
  const [camera, setCamera] = useState('FHAZ'); // Default rover camera
  const [page, setPage] = useState(1); // Default page number
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Function to fetch Mars photos
  const fetchMarsPhotos = useCallback(async () => {
    setLoading(true);
    try {
      const response = await fetch(`/api/mars-photos?sol=${sol}&camera=${camera}&page=${page}`);
      const data = await response.json();
      setMarsPhotos(data.photos || []); // Set Mars photos data
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  }, [sol, camera, page]); // Dependency array includes sol, camera, and page

  // Effect to fetch Mars photos when the component mounts or when dependencies change
  useEffect(() => {
    fetchMarsPhotos();
  }, [fetchMarsPhotos]); // Dependency array includes fetchMarsPhotos

  return (
    <div className="mars-photos-container">
      <Navbar /> {/* Include the Navbar */}

      <div className="mars-photos-content">
        <h1>Mars Rover Photos</h1>

        {/* Input for selecting Martian Sol */}
        <label>
          Enter Sol (Mars Solar Day):
          <input
            type="number"
            value={sol}
            onChange={(e) => setSol(e.target.value)} // Update Sol value
            placeholder="Enter Martian Sol"
            style={{ margin: '10px 0', padding: '10px', fontSize: '1rem' }} // Add inline styles
          />
        </label>

        {/* Select Rover Camera */}
        <label>
          Select a Rover Camera:
          <select
            value={camera}
            onChange={(e) => setCamera(e.target.value)} // Update camera selection
            style={{ margin: '10px 0', padding: '10px', fontSize: '1rem' }} // Add inline styles
          >
            <option value="FHAZ">Front Hazard Avoidance Camera</option>
            <option value="RHAZ">Rear Hazard Avoidance Camera</option>
            <option value="NAVCAM">Navigation Camera</option>
            <option value="PANCAM">Panoramic Camera</option>
            <option value="MINITES">Miniature Thermal Emission Spectrometer</option>
          </select>
        </label>

        {/* Input for Page Number */}
        <label>
          Enter Page Number:
          <input
            type="number"
            value={page}
            onChange={(e) => setPage(e.target.value)} // Update page number
            placeholder="Enter Page Number"
            style={{ margin: '10px 0', padding: '10px', fontSize: '1rem' }} // Add inline styles
          />
        </label>

        <button onClick={fetchMarsPhotos} style={{ margin: '20px 0', padding: '10px', fontSize: '1rem' }}>
          Fetch Photos
        </button>

        {/* Loading and error states */}
        {loading && <div className="spinner">Loading...</div>}
        {error && <p className="error">Error fetching Mars photos: {error.message}</p>}

        {/* Photos gallery */}
        <div className="photos-gallery">
          {marsPhotos.length > 0 ? (
            marsPhotos.map((photo) => (
              <div key={photo.id} className="photo-item">
                <img src={photo.img_src} alt={`Mars ${photo.id}`} className="mars-photo" />
              </div>
            ))
          ) : (
            <p>No photos available for this Sol.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default MarsPhotos;
