import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [apodData, setApodData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch APOD data
  const fetchApodData = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/apod'); // Fetch data from your backend
      const data = await response.json(); // Parse the response as JSON
      console.log('APOD Data:', data); // Log the fetched data to the console
      setApodData(data); // Set the state to the fetched data
    } catch (error) {
      console.error('Error fetching APOD data:', error);
      setError(error); // Set the error state
    } finally {
      setLoading(false); // Set loading to false after fetching
    }
  };

  // Call the fetch function when the component mounts
  useEffect(() => {
    fetchApodData(); // Call the function to fetch APOD data
  }, []);

  return (
    <div className="App">
      <h1>Astronomy Picture of the Day</h1>

      {/* Display loading indicator */}
      {loading && <div className="spinner">Loading...</div>}

      {/* Display error message if there is an error */}
      {error && <p className="error">Error fetching data: {error.message}</p>}

      {/* APOD Section */}
      {apodData && (
        <div className="apod-container">
          <h2>{apodData.title}</h2>
          {/* Check media_type to decide how to display */}
          {apodData.media_type === 'image' ? (
            <img src={apodData.url} alt="APOD" style={{ width: '100%', height: 'auto' }} />
          ) : (
            <iframe
              width="560"
              height="315"
              src={apodData.url}
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          )}
          <p>{apodData.explanation}</p>
        </div>
      )}
    </div>
  );
}

export default App;
