import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import './NasaLibrary.css';

function NasaLibrary() {
  const [searchTerm, setSearchTerm] = useState('');
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`/api/nasa-library?q=${searchTerm}`);
      const data = await response.json();
      if (data.collection && data.collection.items) {
        setImages(data.collection.items);
      } else {
        setImages([]);
        setError('No results found');
      }
    } catch (err) {
      setError('Error fetching NASA library data');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="nasa-library-container">
      <Navbar />
      <h1>NASA Image and Video Library</h1>
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Search for images..."
      />
      <button onClick={handleSearch}>Search</button>
      {loading && <div className="spinner">Loading...</div>}
      {error && <p className="error">{error}</p>}
      <div className="images-grid">
        {images.length > 0 &&
          images.map((item) => (
            <div key={item.data[0].nasa_id} className="image-item">
              <img src={item.links[0].href} alt={item.data[0].title} />
              <p>{item.data[0].title}</p>
            </div>
          ))}
      </div>
    </div>
  );
}

export default NasaLibrary;
