// src/pages/MarsPhotos.js
import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar'; // Ensure the path is correct
import './MarsPhotos.css'; // Import the CSS for MarsPhotos

function MarsPhotos() {
  const [marsPhotos, setMarsPhotos] = useState([]);
  const [sol, setSol] = useState(1000); // Default Sol value
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Function to fetch Mars photos based on Sol
  const fetchMarsPhotos = async () => {
    setLoading(true);
    try {
      const response = await fetch(`/api/mars-photos?sol=${sol}`);
      const data = await response.json();
      setMarsPhotos(data.photos || []); // Set Mars photos data
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch Mars photos when the component mounts or when the Sol value changes
  useEffect(() => {
    fetchMarsPhotos();
  }, [sol]);

  return (
    <div className="mars-photos-container">
      <Navbar /> {/* Include the Navbar */}

      <h1>Mars Rover Photos</h1>
      <input
        type="number"
        value={sol}
        onChange={(e) => setSol(e.target.value)} // Update Sol value
        placeholder="Enter Martian Sol"
      />
      <button onClick={fetchMarsPhotos}>Fetch Photos</button>

      {loading && <div className="spinner">Loading...</div>}
      {error && <p className="error">Error fetching Mars photos: {error.message}</p>}

      <div className="photos-gallery">
        {marsPhotos.length > 0 ? (
          marsPhotos.map((photo) => (
            <div key={photo.id} className="photo-item">
              <img src={photo.img_src} alt={`Mars ${photo.id}`} className="mars-photo" />
            </div>
          ))
        ) : (
          <p>No photos available for this Sol.</p>
        )}
      </div>
    </div>
  );
}

export default MarsPhotos;
