import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [apodData, setApodData] = useState(null);  // State to store APOD data
  const [loading, setLoading] = useState(true); // State to handle loading status
  const [error, setError] = useState(null); // State to handle errors

  // Fetch data from the backend (Node.js server)
  useEffect(() => {
    fetch('/api/apod') // Request to the backend to get APOD data
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setApodData(data); // Store the fetched data in state
        setLoading(false);  // Data has been successfully loaded
      })
      .catch((error) => {
        setError(error); // Set error in state if the request fails
        setLoading(false); // Loading is complete, even if there was an error
      });
  }, []);

  return (
    <div className="App">
      <h1>NASA Astronomy Picture of the Day</h1>

      {/* Show loading message or spinner while data is being fetched */}
      {loading && <div className="spinner"></div>}

      {/* Show error message if there is an error */}
      {error && <p className="error">Error fetching data: {error.message}</p>}

      {/* Display APOD data once it is fetched */}
      {apodData && (
        <div className="apod-container">
          <h2>{apodData.title}</h2>
          <div className="image-container">
            <img src={apodData.url} alt="APOD" />
          </div>
          <p>{apodData.explanation}</p>
          <p><strong>Date:</strong> {apodData.date}</p>
          <p><strong>Copyright:</strong> {apodData.copyright}</p>
          <a href={apodData.hdurl} target="_blank" rel="noopener noreferrer">View HD Image</a>
        </div>
      )}
    </div>
  );
}

export default App;
