import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import './NasaLibrary.css'; // CSS for styling

function NasaLibrary() {
  const [searchTerm, setSearchTerm] = useState('');
  const [libraryItems, setLibraryItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Function to search NASA's library
  const fetchLibraryItems = async (e) => {
    e.preventDefault(); // Prevent the form from reloading the page
    setLoading(true); // Start loading
    setError(null); // Reset errors

    try {
      const response = await fetch(`/api/nasa-library?q=${searchTerm}`); // Search API call
      const data = await response.json();
      setLibraryItems(data); // Set the fetched items
    } catch (error) {
      console.error('Error fetching NASA library data:', error);
      setError('Error fetching data from NASA Library.');
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <div className="nasa-library-container">
      <Navbar />

      <div className="search-section">
        <h1>NASA Image and Video Library</h1>
        <form onSubmit={fetchLibraryItems}>
          <input
            type="text"
            placeholder="Enter search term (e.g., Mars, Earth)"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <button type="submit" className="search-button">Search</button>
        </form>
      </div>

      {loading && <div className="spinner">Loading...</div>}
      {error && <p className="error">{error}</p>}

      <div className="library-grid">
        {libraryItems.length > 0 ? (
          libraryItems.map((item, index) => (
            <div key={index} className="library-item">
              {item.links && item.links[0].href.endsWith('.jpg') && (
                <img src={item.links[0].href} alt={item.data[0].title} className="library-image" />
              )}
              {item.links && item.links[0].href.endsWith('.mp4') && (
                <video src={item.links[0].href} controls className="library-video" />
              )}
              <p>{item.data[0].title}</p>
            </div>
          ))
        ) : (
          !loading && <p>No results found.</p>
        )}
      </div>
    </div>
  );
}

export default NasaLibrary;
