// src/components/APODVisualization.js
import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';

function APODVisualization() {
  const [apodData, setApodData] = useState([]);

  // Fetch APOD data
  const fetchAPOD = async () => {
    const response = await fetch('/api/apod'); // Ensure you have this API
    const data = await response.json();
    setApodData(data); // Assuming this returns multiple entries for visualization
  };

  useEffect(() => {
    fetchAPOD();
  }, []);

  // Prepare data for chart
  const chartData = {
    labels: apodData.map(item => item.date),
    datasets: [
      {
        label: 'APOD Images',
        data: apodData.map(item => item.media_type === 'image' ? 1 : 0),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  return (
    <div>
      <h2>Astronomy Picture of the Day Visualization</h2>
      <Bar data={chartData} />
    </div>
  );
}

export default APODVisualization;
