// src/pages/APOD.js
import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar'; // Import Navbar component
import './APOD.css'; // Ensure you have separate styles for APOD

function APOD() {
  const [apodData, setApodData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAPOD = async () => {
      setLoading(true);
      try {
        const response = await fetch('/api/apod');
        const data = await response.json();
        setApodData(data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchAPOD();
  }, []);

  return (
    <div className="apod-container">
      <Navbar /> {/* Include the Navbar */}

      {loading && <div className="spinner">Loading...</div>}
      {error && <p className="error">Error fetching data: {error.message}</p>}

      {apodData && (
        <div className="apod-content">
          <h1>{apodData.title}</h1>
          {apodData.media_type === 'image' ? (
            <img src={apodData.url} alt={apodData.title} className="apod-image" />
          ) : (
            <iframe
              title="APOD Video"
              src={apodData.url}
              className="apod-video"
              frameBorder="0"
              allow="autoplay; encrypted-media"
              allowFullScreen
            />
          )}
          <p className="description">{apodData.explanation}</p>
          <p className="date">Date: {apodData.date}</p> {/* Display the date */}
        </div>
      )}
    </div>
  );
}

export default APOD;
