import React, { useState, useEffect } from 'react';

function APOD() {
  const [apodData, setApodData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAPOD = async () => {
      try {
        const response = await fetch('/api/apod');
        const data = await response.json();
        setApodData(data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchAPOD();
  }, []);

  return (
    <div>
      <h2>Astronomy Picture of the Day (APOD)</h2>
      <p>This section displays NASA's Astronomy Picture of the Day, featuring stunning images of space along with explanations.</p>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error.message}</p>}
      {apodData && (
        <div>
          <h3>{apodData.title}</h3>
          <img src={apodData.url} alt={apodData.title} style={{ width: '100%' }} />
          <p>{apodData.explanation}</p>
        </div>
      )}
    </div>
  );
}

export default APOD;
