import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

// Register necessary components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function NEOChart({ neoData }) {
  const chartData = {
    labels: neoData.map(neo => neo.name), // NEO names as labels
    datasets: [
      {
        label: 'Miss Distance (km)',
        data: neoData.map(neo => {
          const closeApproachData = neo.close_approach_data[0]; // Get the first close approach data
          const distance = closeApproachData ? parseFloat(closeApproachData.miss_distance.kilometers) : 0; // Fallback to 0
          return distance; 
        }),
        backgroundColor: 'rgba(0, 123, 255, 0.6)', // Bar color
        borderColor: 'rgba(0, 123, 255, 1)', // Border color
        borderWidth: 1,
      }
    ]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Near Earth Objects - Miss Distance',
        color: 'black',
      },
    },
    scales: {
      y: {
        beginAtZero: true, // Ensure the y-axis starts at 0
        title: {
          display: true,
          text: 'Distance (km)', // Add title for y-axis
        },
      },
      x: {
        title: {
          display: true,
          text: 'NEO Names', // Add title for x-axis
        },
      },
    },
  };

  return <Bar data={chartData} options={options} />;
}

export default NEOChart;
