import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

const MyChart = ({ data }) => {
  const chartRef = useRef(null);

  useEffect(() => {
    console.log('Chart Data:', data);  // Log data to check if it is passed correctly
    if (chartRef.current && data.length) {
      const ctx = chartRef.current.getContext('2d');

      const chart = new Chart(ctx, {
        type: 'bar',
        data: {
          labels: data.map((neo) => neo.name),  // Ensure we have the right labels
          datasets: [
            {
              label: 'Miss Distance (km)',
              data: data.map((neo) => neo.missDistance),  // Ensure this data exists
              backgroundColor: 'rgba(75, 192, 192, 0.2)',
              borderColor: 'rgba(75, 192, 192, 1)',
              borderWidth: 1,
            },
          ],
        },
        options: {
          responsive: true,
          plugins: {
            legend: {
              display: false,
            },
          },
          scales: {
            y: {
              beginAtZero: true,
            },
          },
        },
      });

      return () => {
        chart.destroy();  // Cleanup the chart on component unmount
      };
    }
  }, [data]);

  return (
    <div className="chart-container">
      <canvas ref={chartRef}></canvas>
    </div>
  );
};

export default MyChart;
