import React, { useState, useEffect } from 'react';

function APOD() {
  const [apodData, setApodData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAPOD = async () => {
      setLoading(true); // Set loading state before fetching
      try {
        const response = await fetch('/api/apod'); // Fetch data from your backend
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json(); // Parse the response as JSON
        setApodData(data); // Set the state with fetched data
      } catch (error) {
        setError(error); // Set the error state
      } finally {
        setLoading(false); // Set loading to false after fetching
      }
    };

    fetchAPOD(); // Call the function to fetch APOD data
  }, []); // Dependency array should be empty for one-time fetch on mount

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h2>Astronomy Picture of the Day (APOD)</h2>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error.message}</p>}
      {apodData && (
        <div>
          <h3>{apodData.title}</h3>
          <img 
            src={apodData.url} 
            alt={apodData.title} 
            style={{ width: '100%', height: 'auto', borderRadius: '8px' }} 
          />
          <p>{apodData.explanation}</p>
          <p><strong>Date:</strong> {apodData.date}</p>
          {apodData.copyright && <p><strong>Copyright:</strong> {apodData.copyright}</p>}
        </div>
      )}
    </div>
  );
}

export default APOD;
