import React, { useState, useEffect } from 'react';

function APOD() {
  const [apodData, setApodData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]); // Default to today's date

  // Fetch APOD data based on selected date
  const fetchAPOD = async (selectedDate) => {
    setLoading(true); // Set loading state
    try {
      const response = await fetch(`/api/apod?date=${selectedDate}`); // Fetch data from your backend
      const data = await response.json(); // Parse the response as JSON
      console.log('Fetched APOD Data:', data); // Log the fetched data to the console
      setApodData(data); // Store the fetched data
    } catch (error) {
      console.error('Error fetching APOD data:', error);
      setError(error); // Set error state
    } finally {
      setLoading(false); // Set loading to false after fetching
    }
  };

  // Call fetchAPOD when the component mounts or the date changes
  useEffect(() => {
    fetchAPOD(date); // Fetch APOD for the selected date
  }, [date]); // Fetch whenever date changes

  return (
    <div>
      <h2>Astronomy Picture of the Day</h2>
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)} // Update the date when the user selects a new one
      />
      {loading && <div>Loading...</div>}
      {error && <p>Error fetching data: {error.message}</p>}
      {apodData && (
        <div>
          <h3>{apodData.title}</h3>
          {apodData.media_type === 'video' ? (
            <iframe
              title="APOD Video"
              src={apodData.url}
              width="600"
              height="400"
              frameBorder="0"
              allowFullScreen
            ></iframe>
          ) : (
            <img src={apodData.url} alt={apodData.title} style={{ maxWidth: '100%' }} />
          )}
          <p>{apodData.explanation}</p>
          <p><strong>Date:</strong> {apodData.date}</p>
        </div>
      )}
    </div>
  );
}

export default APOD;
