import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function NEOChart({ neoData }) {
  console.log('Chart Data:', neoData); // Check if data is received

  const chartData = {
    labels: neoData.map(neo => neo.name),
    datasets: [
      {
        label: 'Miss Distance (km)',
        data: neoData.map(neo => {
          const closeApproachData = neo.close_approach_data[0];
          const distance = closeApproachData ? parseFloat(closeApproachData.miss_distance.kilometers) : 0;
          return distance;
        }),
        backgroundColor: 'rgba(0, 123, 255, 0.6)',
        borderColor: 'rgba(0, 123, 255, 1)',
        borderWidth: 1,
      }
    ]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Near Earth Objects - Miss Distance',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return <Bar data={chartData} options={options} />;
}

export default NEOChart;
