import React, { useState, useEffect } from 'react';
import './APOD.css'; // Make sure this CSS file exists
import Navbar from '../components/Navbar'; // Adjust the path if necessary

function APOD() {
  const [apodData, setApodData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch APOD data
  const fetchAPOD = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/apod'); // Fetch from your backend
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setApodData(data);
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAPOD(); // Fetch APOD data on mount
  }, []);

  return (
    <div className="apod-container">
      <Navbar /> {/* Include the navbar */}
      <div className="apod-content">
        {loading && <div>Loading...</div>}
        {error && <p>Error fetching data: {error.message}</p>}
        {apodData && (
          <div className="apod-details">
            <h2>{apodData.title}</h2>
            {apodData.media_type === 'video' ? (
              <iframe
                title="APOD Video"
                src={apodData.url}
                width="100%"
                height="500"
                frameBorder="0"
                allowFullScreen
              ></iframe>
            ) : (
              <img src={apodData.url} alt={apodData.title} className="apod-image" />
            )}
            <p className="apod-description">{apodData.explanation}</p>
            <p className="apod-date"><strong>Date:</strong> {apodData.date}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default APOD;
